mapper.insert:
/*
EstimateMapper.insert
등록
*/
insert into estimate (
    estimate_id
    , estimate_date
    , valid_date_cd
    , estimate_status_cd
    , manager_id
    , manager_name
    , manager_tel_no
    , manager_fax_no
    , vendor_id
    , vendor_name
    , vendor_tel_no
    , vendor_fax_no
    , business_id
    , business_name
    , representative_name
    , registration_number
    , business_address
    , business_tel_no
    , business_fax_no
    , business_type
    , industry_type
    , remark
    , reg_id
    , reg_dt
) values (
    #{estimateId}
    , #{estimateDate}
    , #{validDateCd}
    , #{estimateStatusCd}
    , #{managerId}
    , #{managerName}
    , #{managerTelNo}
    , #{managerFaxNo}
    , #{vendorId}
    , #{vendorName}
    , #{vendorTelNo}
    , #{vendorFaxNo}
    , #{businessId}
    , #{businessName}
    , #{representativeName}
    , #{registrationNumber}
    , #{businessAddress}
    , #{businessTelNo}
    , #{businessFaxNo}
    , #{businessType}
    , #{industryType}
    , #{remark}
    , #{regId}
    , now()
)

mapper.update:
/*
EstimateMapper.update
수정
*/
update
    estimate
set
    estimate_date = #{estimateDate}
    , valid_date_cd = #{validDateCd}
    , estimate_status_cd = #{estimateStatusCd}
    , manager_id = #{managerId}
    , manager_name = #{managerName}
    , manager_tel_no = #{managerTelNo}
    , manager_fax_no = #{managerFaxNo}
    , vendor_id = #{vendorId}
    , vendor_name = #{vendorName}
    , vendor_tel_no = #{vendorTelNo}
    , vendor_fax_no = #{vendorFaxNo}
    , business_id = #{businessId}
    , business_name = #{businessName}
    , representative_name = #{representativeName}
    , registration_number = #{registrationNumber}
    , business_address = #{businessAddress}
    , business_tel_no = #{businessTelNo}
    , business_fax_no = #{businessFaxNo}
    , business_type = #{businessType}
    , industry_type = #{industryType}
    , remark = #{remark}
    , upd_id = #{updId}
    , upd_dt = now()
where
    estimate_id = #{estimateId}
