mapper.insert:
/*
UserMapper.insertUser
등록
*/
insert into
    user
        (
            username
            , password
            , name
            , tel_no
            , fax_no
            , role_id
        )
    values
        (
            #{username}
            , #{password}
            , #{name}
            , #{telNo}
            , #{faxNo}
            , 2
        )

mapper.update:
/*
UserMapper.update
수정
*/
update
    user
set
    name = #{name}
    , tel_no = #{telNo}
    , fax_no = #{faxNo}
where
    user_id = #{userId}

/*
UserMapper.updatePassword
수정
*/
update
    user
set
    password = #{password}
where
    user_id = #{userId}
